import React, {useState} from 'react'
import CanvasDraw from "react-canvas-draw";
import CanvasButton from './Button'
export default function CustomDraw(){
  const [brushColor, setBrushColor] = useState('#444');
  const [brushRadius, setBrushRadius]=useState('7');
  return <div>
      <CanvasDraw 
        className="canvas"
         hideGrid="true"
         brushRadius={brushRadius}
         lazyRadius="0"
         canvasWidth="500px"
         canvasHeight="500px"
         brushColor={brushColor}
       /> 

         <div>

         <CanvasButton 
         onClick={()=>{
          setBrushColor('#fff')
         }} 
         icon ="eraser"
         />
        <CanvasButton 
        onClick={()=>{
          setBrushColor('#444')
        }} 
        icon="Pen"
        />
        <CanvasButton onClick={()=>{
           setBrushRadius((Number(brushRadius)+1).toString())
        }}
        icon="+"
        />
         <CanvasButton onClick={()=>{
           setBrushRadius((Number(brushRadius)-1).toString())
        }}
        icon="-"
        />
        <CanvasButton 
          onClick={()=>{
           setBrushColor('red')
        }}
        icon="red"
        />
        <button >red</button>
        <button onClick={()=>{
           setBrushColor('blue')
        }}>blue</button>
        <button onClick={()=>{
         setBrushColor('#444')
        }}>black</button>
       </div>

  </div>
}
