import CodeMirror from '@uiw/react-codemirror';
import 'codemirror/theme/monokai.css';
import 'codemirror/addon/display/autorefresh';
import 'codemirror/addon/comment/comment';
import "codemirror/lib/codemirror.css";
import React,{useState} from 'react'

export default function CodeEditor(){
  const [code, setCode] = useState('code...');
  const [input, setInput] = useState();
  const [language, setLanguage]= useState('c++');
  return <div className="codeEditor">
  <select name="language" id="language" value={language} onChange={(e)=>{
    setLanguage(e.target.value);
  }}>
    <option value="c++">C++</option>
    <option value="c">C</option>
    <option value="python">python</option>
    <option value="javascript">Javascript</option>
  </select>
  <CodeMirror
  className="code"
  onChange={(change)=>{
    setCode(change.getValue())
  }}
  height="70vh"
  width="70vh"
  options={{
    readOnly:false,
    theme: 'monokai',
    tabSize: 2,
    mode:{language}
  }}
/>
<button className="submit" onClick={()=>{
        let codeJSON = JSON.stringify(code);
        let inputJSON = JSON.stringify(input);
        console.log(typeof(language));
        // post this code to compiler APIs
      }}>submit</button>
<div className="IO-box">
      <textarea className="input-area" rows="5" value={input} onChange={(e)=>{
        setInput(e.target.value);
      }} placeholder="Input:"></textarea>
      <textarea className="output-area" rows="5" readOnly></textarea>
    </div>
  </div>
}
