import React, { useState } from 'react';
import './App.css';
import CanvasDraw from "react-canvas-draw";
function App() { 
  const [brushColor, setBrushColor] = useState('#444');
  const [brushRadius, setBrushRadius]=useState('7')
  return (
    <div className="App">
       <CanvasDraw 
        className="canvas"
         hideGrid="true"
         brushRadius={brushRadius}
         lazyRadius="0"
         brushColor={brushColor}
       />    
        <button onClick={()=>{
          setBrushColor('#fff')
        }}>eraser</button>
        <button onClick={()=>{
          setBrushColor('#444')
        }}>Pen</button>
    </div>
  );
}

export default App;
