  const style = {
    width: window.innerWidth/2
  }
  return (
    <div className="App">
    <div className="left-part">
      <textarea 
      style={{width:`${window.innerWidth/2}`}}
      placeholder="Write the Question or the logic.."rows="5"/>
       <CustomCanvas />
    </div>

    </div>
  );
}

export default App;
