import React, {useState} from 'react'
import CanvasDraw from "react-canvas-draw";
import CanvasButton from './Button'
export default function CustomDraw(){
  const [brushColor, setBrushColor] = useState('#444');
  const [brushRadius, setBrushRadius]=useState('7');
  return <div className="canvas-container">
      <CanvasDraw 
        className="canvas"
         hideGrid="true"
         brushRadius={brushRadius}
         lazyRadius="0"
         canvasWidth="70vh"
         canvasHeight="60vh"
         brushColor={brushColor}
       /> 
      <div className="canvas-button-div">

         <CanvasButton 
         onClick={()=>{
          setBrushColor('#fff')
         }} 
         icon ="eraser"
         />
        <CanvasButton 
        onClick={()=>{
          setBrushColor('#444')
        }} 
        icon="pen"
        />
        <CanvasButton onClick={()=>{
           setBrushRadius((Number(brushRadius)+1).toString())
        }}
        icon="plus-circle"
        />
         <CanvasButton onClick={()=>{
           setBrushRadius((Number(brushRadius)-1).toString())
        }}
        icon="minus-circle"
        />
        <CanvasButton 
          onClick={()=>{
           setBrushColor('red')
        }}
        icon="red"
        />
         <CanvasButton 
          onClick={()=>{
           setBrushColor('blue')
        }}
        icon="blue"
        />
         <CanvasButton 
          onClick={()=>{
           setBrushColor('green')
        }}
          icon="green"
        />

       </div>

  </div>
}
