import CodeMirror from '@uiw/react-codemirror';
import 'codemirror/theme/monokai.css';
/// @ts-ignore 
import 'codemirror/keymap/sublime';
import "codemirror/lib/codemirror.css";
import React,{useState} from 'react'

export default function CodeEditor(){
  const [code, setCode] = useState('');
  return <div className="codeEditor"><CodeMirror
  height="70vh"
  width="70vh"
  onChange={(p)=>{
    let newCode = p.display.input.textarea.value;
    setCode(newCode);
  }}
  options={{
    readOnly:false,
    theme: 'monokai',
    tabSize: 2,
    mode: 'c++'
  }}
/>
<div className="IO-box">
      <button onClick={()=>{
        console.log(code)
      }}>submit</button>
      <textarea className="input-area" rows="5" placeholder="Input:"></textarea>
      <textarea className="output-area" rows="5" readOnly></textarea>
    </div>
  </div>
}
